import styles from './SideBar.module.sass';
import React, { useState } from 'react';

const SideBar = ({ comp, setComp }) => {
  const [isOpen, setOpen] = useState(false);
  const toggle = () => setOpen(isOpen => !isOpen);

  return (
    <div className={styles.sideBar}>
      <img onClick={() =>toggle()} src='data:image/png;base64,'/>
      <ul className={isOpen ? styles.show : styles.hide}>
        <a>관리 페이지</a> <br />
        <span onClick={() => setComp(1)}>
          동아리 소개
        </span> <br/> 
        <span onClick={() => setComp(2)}>
          활동내용
        </span> <br/>
        <span onClick={() => setComp(3)}>
          동아리 일정
        </span> <br/>
        <span onClick={() => setComp(4)}>
          공지게시판
        </span> <br/>
        <span onClick={() => setComp(5)}>
          자유게시판
        </span> <br/>
        <span onClick={() => setComp(6)}>
          동아리 후기
        </span> <br/>
      </ul>
    </div>
  )
}

export default SideBar;